const inputData = $input.all();
const results = [];

for (const item of inputData) {
  try {
    // 1. æå– output å­—æ®µä¸­çš„ JSON å†…å®¹
    let outputText = item.json.output || '';

    // ç§»é™¤ markdown ä»£ç å—æ ‡è®°
    outputText = outputText.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

    // è§£æ JSON
    const parsedData = JSON.parse(outputText);

    // 2. æ£€æŸ¥æ˜¯å¦æˆåŠŸ
    if (!parsedData.success || !parsedData.clips || parsedData.clips.length === 0) {
      results.push({
        json: {
          success: false,
          message: parsedData.message || 'æœªæ‰¾åˆ°å‰ªè¾‘ç‰‡æ®µ',
          original_data: parsedData,
          file_urls: [],
          total_duration: 0,
          clip_count: 0
        }
      });
      continue;
    }

    // 3. æŒ‰é¡ºåºæå–æ‰€æœ‰ file_url
    const fileUrls = parsedData.clips
      .sort((a, b) => a.order - b.order)  // æŒ‰ order æ’åº
      .map(clip => clip.file_url);

    // 4. è®¡ç®—ç´¯è®¡æ—¶é•¿
    const totalDuration = parsedData.clips.reduce((sum, clip) => sum + (clip.duration || 0), 0);

    // 5. ç”Ÿæˆè¾“å‡ºç»“æ„
    results.push({
      json: {
        success: true,

        // æ ¸å¿ƒæ•°æ®ï¼šURL æ•°ç»„
        file_urls: fileUrls,

        // ç»Ÿè®¡ä¿¡æ¯
        total_duration: totalDuration,
        total_duration_formatted: formatDuration(totalDuration),
        clip_count: parsedData.clips.length,

        // è¯¦ç»†ä¿¡æ¯
        clips_summary: parsedData.clips.map(clip => ({
          order: clip.order,
          type: clip.type,
          file_url: clip.file_url,
          duration: clip.duration,              // å‰ªè¾‘åæ—¶é•¿
          original_duration: clip.original_duration || clip.duration,  // åŸå§‹è§†é¢‘æ—¶é•¿
          start_time: clip.start_time || 0,     // å‰ªè¾‘èµ·å§‹ç‚¹ï¼ˆç§’ï¼‰
          end_time: clip.end_time || clip.duration,  // å‰ªè¾‘ç»“æŸç‚¹ï¼ˆç§’ï¼‰
          needs_editing: (clip.original_duration && clip.original_duration > clip.duration),  // æ˜¯å¦éœ€è¦å‰ªè¾‘
          dialogue: clip.dialogue
        })),

        // åŸå§‹å®Œæ•´æ•°æ®
        original_data: parsedData
      }
    });

  } catch (error) {
    // é”™è¯¯å¤„ç†
    results.push({
      json: {
        success: false,
        error: error.message,
        raw_output: item.json.output,
        file_urls: [],
        total_duration: 0,
        clip_count: 0
      }
    });
  }
}

// è¾…åŠ©å‡½æ•°ï¼šæ ¼å¼åŒ–æ—¶é•¿ï¼ˆç§’ -> MM:SSï¼‰
function formatDuration(seconds) {
  const minutes = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${minutes}:${secs.toString().padStart(2, '0')}`;
}

return results;

// å°†ä¸Šæ¸¸çš„ URL æ•°ç»„è½¬æ¢ä¸º concatenate æ¥å£æ‰€éœ€çš„æ ¼å¼ï¼Œå¹¶æ›¿æ¢ç½‘å…³åœ°å€
const inputData = $input.first().json;
const urlArray = inputData['è¿ç»­æ‹¼æ¥url'];

