// é€’å½’æŸ¥æ‰¾æ‰€æœ‰è§†é¢‘ç‰‡æ®µ
function findClips(obj, clips = []) {
  if (!obj) return clips;

  // å¦‚æœæ˜¯æ•°ç»„ï¼Œé€’å½’å¤„ç†æ¯ä¸ªå…ƒç´ 
  if (Array.isArray(obj)) {
    for (const item of obj) {
      findClips(item, clips);
    }
    return clips;
  }

  // å¦‚æœæ˜¯å¯¹è±¡
  if (typeof obj === 'object') {
    // æ£€æŸ¥æ˜¯å¦æ˜¯è§†é¢‘ç‰‡æ®µå¯¹è±¡
    if (obj.file_url && obj.start_time !== undefined && obj.end_time !== undefined) {
      // æ›¿æ¢ URL
      let videoUrl = obj.file_url.replace('host.docker.internal', 'minio');

      clips.push({
        video_url: videoUrl,
        start_time: obj.start_time,
        end_time: obj.end_time
      });

      console.log(`æ‰¾åˆ°ç‰‡æ®µ: ${videoUrl}`);
    }

    // é€’å½’å¤„ç†å¯¹è±¡çš„æ‰€æœ‰å±æ€§
    for (const key in obj) {
      findClips(obj[key], clips);
    }
  }

  return clips;
}

